%@@@@@@@@@@@@@@@@@@@@@  A 2status switch-nonswicth     @@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@
%@@@@@@@@@@@@@@@@@@@@@  A 2status switch-nonswicth     @@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@
close all;
invarlist ={'A-A','A-A','V-A','V-A'};
listcond=[[7;7] [5;7] [10;5] [8;5]];
alphath=0.05;

[halfwidthindvI,pindvI,statsindvI,tblindvI,meansindvI,resultsindvI,critindvI,seindvI,pvalindvI] = AnovaIndAnalysisVecOut(strcat(Outdir,'\A_2states\'),alphath,indseqcondcell,id,invarlist,listcond);
