close all;
invarlist ={'V-V','V-V','A-V','A-V'};
listcond=[[8;8] [10;8] [5;10] [7;10]];
alphath=0.05;

[halfwidthindvII,pindvII,statsindvII,tblindvII,meansindvII,resultsindvII,critindvII,seindvII,pvalindvII] = AnovaIndAnalysisVecOut(strcat(Outdir,'\V_2states\'),alphath,indseqcondcell,id,invarlist,listcond);
