invarlist ={'A-A-A','V-A-A','A-V-A','V-V-A'};
listcond=[[7;7] [5;7] [10;5] [8;5]];
alphath=0.05;

[pindv,statsindv,meansindv,resultsindv] = AnovaIndAnalysis(strcat(Outdir,'\A_4states\'),alphath,indseqcondcell,id,invarlist,listcond);
